(3526, 0, 'Thư viện IELTS', '', '', 0, 0, 1, 1, '/tin-tuc/thu-vien-ielts-c3526.html', 1, 'Thư viện IELTS -  sách luyện thi IELTS do Aland English biên soạn', 'Thư viện IELTS là tổng hợp các đầu sách IELTS do các chuyên gia IELTS tại Aland English biên soạn. Download PDF và audio hoàn toàn miễn phí.', 'thư viện IELTS, tủ sách IELTS, sách IELTS, band 8, Aland English, luyện thi IELTS', 1553135580, 1553135580, NULL, '', NULL),
(3527, 0, 'Luyện thi IELTS Online', '', 'Luyện thi IELTS Online - tập hợp các bài luyện thi Online của Aland English. Bài học bao gồm Video + PDF và bài tập luyện tập đính kèm. Học IELTS hoàn toàn miễn phí cùng Aland English', 0, 3501, 1, 1, '/tin-tuc/luyen-thi-ielts-online-c3527.html', 1, 'Luyện thi IELTS Online - tổng hợp bài học IELTS online miễn phí - Aland English', 'Tổng hợp các bài học luyện thi IELTS Online từ chuyên gia 8.5 IELTS; đầy đủ PDF, audio và video để giúp các bạn học tập. Học IELTS miễn phí cùng Aland English', 'luyện thi IELTS, luyện thi IELTS Online, luyen thi IELTS, luyen thi ielts online, ielts online', 1553217474, 1553218347, NULL, '', NULL),
(3528, 0, 'Lịch khai giảng', '', '', 0, 0, 1, 1, '/tin-tuc/lich-khai-giang-c3528.html', 0, 'Lịch khai giảng các lớp luyện thi IELTS tại Hà Nội - Aland English', 'Thông tin, lịch khai giảng các lớp luyện thi IELTS của Aland English tại Hà Nội. Cập nhật lịch học liên tục và nhanh nhất năm 2019', '', 1554449098, 1554449098, NULL, '', NULL),
