INSERT INTO `test_result` (`id`, `type`, `score_min`, `score_max`, `result`, `suggest`, `create_time`, `update_time`, `status`) VALUES
(2, 'listening', '1.0', '3.0', '<p><span style=\"font-size:18px;\"><strong>Oa. Bạn cần cố gắng hơn. Hành trình chinh phục IELTS chỉ mới bắt đầu mà thôi.</strong></span></p>\r\n', '<p><strong>Aland IELTS </strong>khuyên bạn nên luyện nghe với những nguồn đơn giản trước để làm quen với việc nghe tiếng Anh và bắt đầ với kì thi IELTS, ví dụ như phim ảnh.</p>\r\n\r\n<p>Bên cạnh đó, bạn có thể tham khảo một số cuốn sách về kỹ năng Listening như sau:</p>\r\n\r\n<ul>\r\n <li>Listen carefully</li>\r\n <li>Basic tactics for Listening (Cuốn 1)</li>\r\n</ul>\r\n', 1560992438, 1560992933, 1),
(3, 'listening', '3.5', '4.5', '<p><span style=\"font-size:18px;\"><strong>WOW! Bạn đã làm khá TỐT. Nhưng vẫn cần cố gắng hơn nhé.</strong></span></p>\r\n', '<p>Bạn đã nghe được phần lớn nội dung của bài thi. Tuy nhiên, có thể do chưa nắm rõ được cấu trúc của bài thi hoặc chưa có chiến thuật làm bài nghe hiệu quả.</p>\r\n\r\n<p>Vì thế, <strong>Aland IELTS </strong>khuyên bạn cần luyện nghe thật nhiều, kết hợp tham khảo 2 cuốn sách dưới đây:</p>\r\n\r\n<ul>\r\n <li>Basic tactics for Listening (Cuốn 2)</li>\r\n <li>Collins - Get ready for IELTS Listening</li>\r\n</ul>\r\n', 1560992524, 1560992830, 1),
(4, 'listening', '5.0', '6.5', '<p><span style=\"font-size:18px;\"><strong>Conglaturation! Bạn đã làm rất TỐT.</strong></span></p>\r\n', '<p>Bạn đã có thể nghe hiểu phần lớn nội dung trong phần thi IELTS Listening. Tuy nhiên, do chưa nắm vững các kỹ thuật làm bài, phản xạ và tư duy câu trả lời chưa thực sự tốt nên kết quả chưa cao.</p>\r\n\r\n<p>Bởi vậy, <strong>Aland IELTS</strong> khuyên bạn nên tham khảo một số tài liệu như:</p>\r\n\r\n<ul>\r\n <li>Intensive IELTS Listening</li>\r\n <li>IELTS Strategies for Listening</li>\r\n <li>Collins Listening for IELTS</li>\r\n</ul>\r\n', 1560992587, 1560992794, 1),
(5, 'listening', '7.0', '8.0', '<p><strong><span style=\"font-size:18px;\">Fantastic! Bạn thật tuyệt vời.</span></strong></p>\r\n', '<p>Khả năng nghe của bạn đã khá tốt. Nếu muốn cải thiện điểm của phần này, Aland khuyên bạn nên dành nhiều thời gian hơn cho việc luyện đề, tăng phản xạ và tăng tốc làm bài bằng cách luyện đề. Bạn có thể luyện tập với những bộ đề thi như:</p>\r\n\r\n<ul>\r\n <li>Cambridge: Practice for IELTS (Vol 7-14)</li>\r\n <li>Vol 4</li>\r\n <li>Test Plus</li>\r\n</ul>\r\n', 1560992654, 1560992767, 1),
(6, 'listening', '8.5', '9.0', '<p><strong><span style=\"font-size:18px;\">Unbelivebale! Bạn quá tuyệt.</span></strong></p>\r\n', '<p>Bạn là một người sử dụng tiếng Anh rất thành thạo. Với tầm điểm Listening và Reading cao như vậy, bạn chỉ cần tập trung thêm một chút vào 2 kỹ năng khó nhằn hơn của bài thi là Writing và Speaking, vậy thì bạn sẽ nắm chắc trong tay một mức điểm số IELTS “khủng\" rồi đó!</p>\r\n', 1560992699, 1560992699, 1),
(7, 'reading', '1.0', '3.0', '<p><span style=\"font-size:18px;\"><strong>Bạn cần cố gắng hơn. Hành trình chinh phục IELTS chỉ mới bắt đầu mà thôi.</strong></span></p>\r\n', '<p><strong>Aland IELTS</strong> khuyên bạn nên học lại những chủ điểm ngữ pháp và từ vựng quan trọng thường xuyên xuất hiện trong bài thi IELTS:</p>\r\n\r\n<ul>\r\n <li>Ngữ pháp: 5 thì cơ bản (hiện tại đơn, hiện tại tiếp diễn, hiện tại hoàn thành, quá khứ đơn, tương lai đơn); Từ loại (danh - động - tính - trạng); S-V agreement (sự hoà hợp chủ ngữ - động từ); Mệnh đề quan hệ…</li>\r\n <li>Từ vựng theo chủ đề như: Family, Friends, Accommodation, Hobby...</li>\r\n</ul>\r\n\r\n<p>Bạn có thể tham khảo thêm các tài liệu như:</p>\r\n\r\n<ul>\r\n <li>Collins - Grammar for IELTS</li>\r\n <li>Collins - Vocabulary for IELTS</li>\r\n</ul>\r\n', 1560993024, 1560993024, 1),
(8, 'reading', '3.5', '4.5', '<p><strong><span style=\"font-size:18px;\">WOW! Bạn đã làm khá TỐT. Nhưng vẫn cần cố gắng hơn nhé.</span></strong></p>\r\n', '<p>Bạn nên mở rộng vốn từ vựng, ngữ pháp của mình và tìm hiểu thêm các Tips để làm bài Reading hiệu quả hơn.</p>\r\n\r\n<p><strong>Aland IELTS </strong>khuyên bạn nên tham khảo một số cuốn sách như:</p>\r\n\r\n<ul>\r\n <li>Skimming and Scanning</li>\r\n <li>Complete IELTS band 4.0 – 5.0</li>\r\n <li>Collins Get ready for IELTS Reading</li>\r\n</ul>\r\n', 1560993069, 1560993069, 1),
(9, 'reading', '5.0', '6.5', '<p><span style=\"font-size:18px;\"><strong>Conglaturation! Bạn đã làm rất TỐT.</strong></span></p>\r\n', '<p>Bạn đã có thể đọc hiểu phần lớn nội dung của bài đọc, nhưng đôi lúc vẫn gặp phải những khó khăn trong với từ vựng và cấu trúc phức tạp.</p>\r\n\r\n<p>Vì vậy, đây là lúc bạn nên tham khảo thêm các nội dung chính thống bằng tiếng Anh để trau dồi thêm vốn từ vựng học thuật, cũng như nâng cao khả năng đọc hiểu của mình (Ví dụ: The New York Times, The Washington Posts…).</p>\r\n\r\n<p><strong>Tài liệu tham khảo thêm:</strong></p>\r\n\r\n<ul>\r\n <li>Complete IELTS band 5.0 – 6.5</li>\r\n <li>Intensive IELTS Reading</li>\r\n <li>IELTS Strategies for Reading</li>\r\n</ul>\r\n', 1560993123, 1560993123, 1),
(10, 'reading', '7.0', '8.0', '<p><strong><span style=\"font-size:18px;\">Fantastic! Bạn thật tuyệt vời.</span></strong></p>\r\n', '<p>Khả năng đọc hiểu của bạn đã ở mức khá vững. Vì vậy, để cải thiện số điểm phần IETLS Reading tốt hơn, Aland khuyên bạn nên tập trung dành nhiều thời gian để luyện đề và tăng tốc độ làm bài thông qua sách luyện đề:</p>\r\n\r\n<ul>\r\n <li>Cambridge: Practice tests for IELTS (7-14)</li>\r\n</ul>\r\n', 1560993187, 1560993187, 1),
(11, 'reading', '8.5', '9.0', '<p><span style=\"font-size:18px;\"><strong>Unbelivebale! Bạn quá tuyệt.</strong></span></p>\r\n', '<p>Bạn là một người sử dụng tiếng Anh rất thành thạo. Với tầm điểm Listening và Reading cao như vậy, bạn chỉ cần tập trung thêm một chút vào 2 kỹ năng khó nhằn hơn của bài thi là Writing và Speaking, vậy thì bạn sẽ nắm chắc trong tay một mức điểm số IELTS “khủng\" rồi đó!</p>\r\n', 1560993217, 1560993217, 1);
